mapper.select:
select commodity.id,commodity.name,commodity.barcode,commodity.inventory,commodity.purchase_price, commodity.price,commodity.specification,commodity.is_discount,commodity.wholesale_price, commodity.produce_date,commodity.expiration_time,commodity.cla_id, commodity.unit_id, commodity.supplier_id,commodity.sup_id,commodity.is_multibarcode, classification.classification, commodity.create_time,unit.unit, supplier.name as supplier_name, supplier.number from commodity left outer join supplier on commodity.supplier_id = supplier.id and supplier.is_deleted = 0 left outer join classification on classification.id = commodity.cla_id and classification.is_deleted = 0 left outer join unit on commodity.unit_id = unit.id and unit.is_deleted = 0
commodity.sup_id = #{commodity.supId} and commodity.is_deleted = 0
and commodity.cla_id = #{commodity.claId}

select commodity.id,commodity.name,commodity.barcode,commodity.inventory,commodity.purchase_price, commodity.price,commodity.specification,commodity.is_discount,commodity.wholesale_price, commodity.produce_date,commodity.expiration_time,commodity.cla_id, commodity.unit_id, commodity.supplier_id,commodity.sup_id,commodity.is_multibarcode, classification.classification, commodity.create_time,unit.unit, supplier.name as supplier_name, supplier.number from commodity left outer join supplier on commodity.supplier_id = supplier.id and supplier.is_deleted = 0 left outer join classification on classification.id = commodity.cla_id and classification.is_deleted = 0 left outer join unit on commodity.unit_id = unit.id and unit.is_deleted = 0
commodity.sup_id = #{commodity.supId} and commodity.is_deleted = 0
and commodity.cla_id = #{commodity.claId}
order by CONVERT(${sortDTO.prop} USING gbk) COLLATE gbk_chinese_ci ${sortDTO.order}

select commodity.id,commodity.name,commodity.barcode,commodity.inventory,commodity.purchase_price, commodity.price,commodity.specification,commodity.is_discount,commodity.wholesale_price, commodity.produce_date,commodity.expiration_time,commodity.cla_id, commodity.unit_id, commodity.supplier_id,commodity.sup_id,commodity.is_multibarcode, classification.classification, commodity.create_time,unit.unit, supplier.name as supplier_name, supplier.number, multibarcode.barcode as m_barcode from commodity left outer join supplier on commodity.supplier_id = supplier.id and supplier.is_deleted = 0 left outer join classification on classification.id = commodity.cla_id and classification.is_deleted = 0 left outer join unit on commodity.unit_id = unit.id and unit.is_deleted = 0 left outer join multibarcode on multibarcode.com_id = commodity.id and multibarcode.is_deleted = 0
commodity.sup_id = #{commodity.supId} and commodity.is_deleted = 0
and (commodity.name like '%${commodity.name}%' or commodity.barcode like '%${commodity.barcode}%' or multibarcode.barcode like '%${commodity.barcode}%')
and commodity.cla_id = #{commodity.claId}
order by CONVERT(${sortDTO.prop} USING gbk) COLLATE gbk_chinese_ci ${sortDTO.order}

select commodity.id,commodity.name,commodity.barcode,commodity.inventory,commodity.purchase_price, commodity.price,commodity.specification,commodity.is_discount,commodity.wholesale_price, commodity.produce_date,commodity.expiration_time,commodity.cla_id, commodity.unit_id, commodity.supplier_id,commodity.sup_id,commodity.is_multibarcode, classification.classification, commodity.create_time,unit.unit, supplier.name as supplier_name, supplier.number, multibarcode.barcode as m_barcode from commodity left outer join supplier on commodity.supplier_id = supplier.id and supplier.is_deleted = 0 left outer join classification on classification.id = commodity.cla_id and classification.is_deleted = 0 left outer join unit on commodity.unit_id = unit.id and unit.is_deleted = 0 left outer join multibarcode on multibarcode.com_id = commodity.id and multibarcode.is_deleted = 0
commodity.sup_id = #{commodity.supId} and commodity.is_deleted = 0
and classification.id in
#{item}
and supplier.id in
#{item}
and commodity.is_discount = #{commodity.isDiscount}
and #{commodity.createTime} < commodity.create_time
and commodity.create_time < #{secondCommodity.createTime}
and #{commodity.price} < commodity.price
and commodity.price < #{secondCommodity.price}
and #{commodity.inventory} < commodity.inventory
and commodity.inventory < #{secondCommodity.inventory}
and (commodity.name like '%${commodity.name}%' or commodity.barcode like '%${commodity.barcode}%' or multibarcode.barcode like '%${commodity.barcode}%')
and commodity.specification = #{commodity.specification}
and commodity.is_multibarcode = #{commodity.isMultibarcode}
order by CONVERT(${sortDTO.prop} USING gbk) COLLATE gbk_chinese_ci ${sortDTO.order}
